import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Home = () => {
  const getTeams = async () => {
    const response = await axios.get('https://statsapi.web.nhl.com/api/v1/teams')
    const body = await response.data
    console.log('data : ', body.teams)
    return body.teams
  }

  const [teams, setTeams] = useState([])

  useEffect(() => {
    let isSubscribed = true

    getTeams()
    .then(res => isSubscribed && setTeams(res))
    .catch(err => console.log(err))

    return () => isSubscribed = false
  }, [])

  return (
    <section className='Home'>
      {teams.map((team, i) => <div><p>Name : {team.name}</p> <img src={'https://www.nhl.com/site-core/images/team/logo/current/' + team.franchise.franchiseId + '_dark.svg'} /></div>)}
    </section>
  );
}

export default Home;
