import React, { useState, useEffect } from 'react'

import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
import Favorite from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';

function Vote(props) {
  const [teams, setTeams] = useState([])

  const handleVote = e => {
    console.log('checked : ', e.target.checked)
    const teamID = document.getElementById(e.target.id).teamid
    console.log('teamID : ', teamID)
  }

  return (
    <div>
      <img src={'https://www-league.nhlstatic.com/images/logos/teams-current-primary-light/' + props.teams.id + '.svg'} alt={props.teams.name} />
      <FormControlLabel
        control={<Checkbox icon={<FavoriteBorder />} checkedIcon={<Favorite />} teamid={props.teams.id} id={props.teams.name} value={props.teams.name} onChange={handleVote} />}
        label={props.teams.name}
      />
    </div>
  )
}

export default Vote
