import React, { useState, useEffect } from 'react'
import axios from 'axios'

import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Favorite from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';

function Vote(props) {
  const [teams, setTeams] = useState([])
  const [teamsSelected, setTeamsSelected] = useState([])
  const [teamsCount, setTeamsCount] = useState(0)

  const handleVote = e => {
    if (e.target.checked && teamsCount < 16) {
      setTeamsSelected([...teamsSelected, e.target.value])
      setTeamsCount(teamsCount + 1)
    }
    if(!e.target.checked) {
      console.log('oldTeamsSelected : ', teamsSelected)
      teamsSelected.splice(teamIndex, 1)
      setTeamsSelected(teamsSelected)
      setTeamsCount(teamsCount - 1)
    }
  }

  const getTeams = async () => {
    const response = await axios.get('https://statsapi.web.nhl.com/api/v1/teams')
    const body = await response.data
    return body.teams
  }

  useEffect(() => {
    let isSubscribed = true

    getTeams()
    .then(res => isSubscribed && setTeams(res))
    .catch(err => console.log(err))

    return () => isSubscribed = false
  }, [])

  return (
    <div>
      <section>
        Nombre d'équipe sélectionnée : {teamsCount}
      </section>
      <section className='Home-vote'>
        {teams.map((team, i) => (
          <div key={team.name}>
            <img src={'https://www-league.nhlstatic.com/images/logos/teams-current-primary-light/' + team.id + '.svg'} alt={team.name} />
            <FormControlLabel
              control={<Checkbox icon={<FavoriteBorder />} checkedIcon={<Favorite />} value={team.name} onChange={handleVote} />}
              label={team.name}
            />
          </div>
        ))}
      </section>
    </div>
  )
}

export default Vote
